<?php

/**
 * BQElementTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class BQElementTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object BQElementTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('BQElement');
    }

    public static function getRecordsByBQLibraryAndIds(BQLibrary $bqLibrary, array $ids)
    {
        return DoctrineQuery::create()
        ->from('BQElement t')
        ->whereIn('t.id', $ids)
        ->andWhere('t.library_id = ?', array($bqLibrary->id))
        ->addOrderBy('t.priority ASC')
        ->fetchArray();
    }
}