class BQItemFormulatedColumn extends BaseBQItemFormulatedColumn
{
    public function save(Doctrine_Connection $conn = null)
    {
        parent::save($conn);

        if($this->deleted_at == NULL)
        {
            $this->refresh();

            $this->clearBuildUpRates($conn);//reset all build up rates
        }
    }

    public function clearBuildUpRates(Doctrine_Connection $conn = null)
    {
        if($this->column_name == BQItem::FORMULATED_COLUMN_RATE and !$this->has_build_up)
        {
            BQItemTable::deleteBuildUpRatesByBQItemId($this->relation_id);
        }
    }
}
