class BQLibraryBuildUpRateResource extends BaseBQLibraryBuildUpRateResource
{
    public function delete(Doctrine_Connection $conn = null)
    {
        $buildUpRateItems = $this->Items->toArray();

        $buildUpRateItemIds = Utilities::arrayValueRecursive('id', $buildUpRateItems);

        parent::delete($conn);

        if(count($buildUpRateItemIds) > 0 && $buildUpRateItemIds[0])
        {
            $implodedIds = implode(',', $buildUpRateItemIds);

            Doctrine_Manager::getInstance()->getCurrentConnection()
                ->fetchAssoc("UPDATE ".BQLibraryBuildUpRateItemTable::getInstance()->getTableName()." SET deleted_at = NOW() WHERE id IN (".$implodedIds.") AND deleted_at IS NULL");

            $query = Doctrine_Query::create()
                ->delete('BQLibraryBuildUpRateEdge e')
                ->where('node_from IN (SELECT c.id FROM BQLibraryBuildUpRateFormulatedColumn c WHERE c.relation_id IN ('.$implodedIds.') AND c.deleted_at IS NULL)')
                ->execute();

            $query = Doctrine_Query::create()
                ->delete('BQLibraryBuildUpRateEdge e')
                ->where('node_to IN (SELECT c.id FROM ScheduleOfRateBuildUpRateFormulatedColumn c WHERE c.relation_id IN ('.$implodedIds.') AND c.deleted_at IS NULL)')
                ->execute();

            Doctrine_Manager::getInstance()->getCurrentConnection()
                ->fetchAssoc("UPDATE ".BQLibraryBuildUpRateFormulatedColumnTable::getInstance()->getTableName()." SET deleted_at = NOW() WHERE relation_id IN (".$implodedIds.") AND deleted_at IS NULL");
        }
    }
