<?php

/**
 * BillPageTable
 *
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class BillPageTable extends Doctrine_Table {

	/**
	 * Returns an instance of this class.
	 *
	 * @return object BillPageTable
	 */
	public static function getInstance()
	{
		return Doctrine_Core::getTable('BillPage');
	}

	public static function updateHasAddendum(Array $pagesId, $newRevisionId)
	{
		Doctrine_Query::create()
			->update('BillPage')
			->set('new_revision_id', '?', $newRevisionId)
			->whereIn('id', $pagesId)
			->andWhere('new_revision_id is NULL')
			->execute();
	}

}