class ClaimCertificateTax extends BaseClaimCertificateTax
{
    public function canBeDeleted()
    {
        return true;
    }

    public function updatePriority($isOperationAdd)
    {       
        $entries = DoctrineQuery::create()->select('cct.id, cct.priority')
            ->from('ClaimCertificateTax cct')
            ->where('cct.id != ?', $this->id)
            ->andWhere('cct.priority >= ?', $this->priority)
            ->setHydrationMode(Doctrine_Core::HYDRATE_ARRAY)
            ->execute();

        $pdo = ClaimCertificateTaxTable::getInstance()->getConnection()->getDbh();

        foreach($entries as $entry)
        {   
            $priority = $isOperationAdd ? ($entry['priority'] + 1) : ($entry['priority'] - 1);
            $stmt = $pdo->prepare("UPDATE " . ClaimCertificateTaxTable::getInstance()->getTableName() . " SET priority=:priority WHERE id=:id;");
            $stmt->bindParam(':id', $entry['id'], PDO::PARAM_INT);
            $stmt->bindParam(':priority', $priority, PDO::PARAM_INT);
            $stmt->execute();
        }
    }

}
