<?php

/**
 * CompanyGroupTable
 *
 * This class has NOT been auto-generated by the Doctrine ORM Framework
 */
class CompanyGroupTable extends Doctrine_Table {
    /**
     * Returns an instance of this class.
     *
     * @return object CompanyGroupTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('CompanyGroup');
    }

    public static function addRelation($groupId, $companyId)
    {
        $pdo = self::getInstance()->getConnection()->getDbh();

        $stmt = $pdo->prepare("INSERT INTO " . strtolower(CompanyGroupTable::getInstance()->getTableName()) . " VALUES (:companyId, :groupId)");

        return $stmt->execute(array(
            'groupId'   => $groupId,
            'companyId' => $companyId,
        ));
    }
}