class CostDataPrimeCostSumColumn extends BaseCostDataPrimeCostSumColumn
{
    CONST COLUMN_AMOUNT = 'amount';

    public function save(Doctrine_Connection $conn = null)
    {
        $amountColumnUpdated = $this->amountColumnUpdated();

        parent::save($conn);

        if( $amountColumnUpdated )
        {
            $this->flushLinks();
        }
    }

    protected function amountColumnUpdated()
    {
        return key_exists(self::COLUMN_AMOUNT, $this->getModified());
    }

    protected function flushLinks()
    {
        Doctrine_Manager::getInstance()->getCurrentConnection()
            ->fetchAssoc("UPDATE ".CostDataPrimeCostSumColumnTable::getInstance()->getTableName()." SET conversion_factor = 1 WHERE id = {$this->id} AND deleted_at IS NULL");

        Doctrine_Manager::getInstance()->getCurrentConnection()
            ->fetchAssoc("DELETE FROM ".BillItemCostDataPrimeCostSumColumnTable::getInstance()->getTableName()." WHERE cost_data_prime_cost_sum_column_id = {$this->id}");
    }
}
