class CostDataPrimeCostSumItem extends BaseCostDataPrimeCostSumItem
{
    CONST COLUMN_APPROVED_COST = 'approved_cost';
    CONST COLUMN_AWARDED_COST = 'awarded_cost';

    public function save(Doctrine_Connection $conn = null)
    {
        $awardedCostUpdated = $this->awardedCostUpdated();

        parent::save($conn);

        if( $awardedCostUpdated )
        {
            $this->flushLinks();
        }
    }

    protected function awardedCostUpdated()
    {
        return key_exists(self::COLUMN_AWARDED_COST, $this->getModified());
    }

    protected function flushLinks()
    {
        Doctrine_Manager::getInstance()->getCurrentConnection()
            ->fetchAssoc("UPDATE ".CostDataPrimeCostSumItemTable::getInstance()->getTableName()." SET conversion_factor = 1 WHERE id = {$this->id} AND deleted_at IS NULL");

        Doctrine_Manager::getInstance()->getCurrentConnection()
            ->fetchAssoc("DELETE FROM ".BillItemCostDataPrimeCostSumItemTable::getInstance()->getTableName()." WHERE cost_data_prime_cost_sum_item_id = {$this->id}");
    }
}
