<?php

/**
 * CurrencyTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class CurrencyTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object CurrencyTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('Currency');
    }

    public static function selections()
    {
        $data = array();

        $currencies = Doctrine_Query::create()
        ->select('c.id, c.currency_code')
        ->from('Currency c')
        ->fetchArray();

        foreach ( $currencies as $currency )
        {
            $data[] = array(
                'id'   => (string) $currency['id'],
                'name' => $currency['currency_code'],
            );
        }

        return $data;
    }
}