<?php

/**
 * DimensionTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class DimensionTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object DimensionTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('Dimension');
    }

    public static function canBeDeletedById($id)
    {
        $pdo = self::getInstance()->getConnection()->getDbh();

        $stmt = $pdo->prepare("SELECT COUNT(id) FROM ".UnitOfMeasurementDimensionsTable::getInstance()->getTableName()." WHERE dimension_id = ".$id);

        $stmt->execute();

        $count = $stmt->fetch(PDO::FETCH_COLUMN, 0);

        return $count > 0 ? false : true;
    }
}