class EditorBillItemFormulatedColumn extends BaseEditorBillItemFormulatedColumn
{
    public function save(Doctrine_Connection $conn = null)
    {
        parent::save($conn);

        if($this->deleted_at == NULL)
        {
            $this->refresh();

            if($this->column_name == BillItem::FORMULATED_COLUMN_RATE)
            {
                //Update Type Total Amount
                $this->EditorBillItemInfo->updateTypeTotalAmount();
            }
        }
    }
}
