class EditorBillItemPrimeCostRate extends BaseEditorBillItemPrimeCostRate
{
    public function save(Doctrine_Connection $conn = null)
    {
        parent::save($conn);

        $this->refresh();

        $formulatedColumn = EditorBillItemFormulatedColumnTable::getInstance()->getByRelationIdAndColumnName($this->bill_item_info_id, BillItem::FORMULATED_COLUMN_RATE);

        $formulatedColumn->setFormula($this->total);

        $formulatedColumn->linked = false;

        $formulatedColumn->save($conn);
                
        $this->EditorBillItemInfo->updateBillItemTotalColumns();
    }
}
