 */
class NominatedSubContractorInformationTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return NominatedSubContractorInformationTable The table instance
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('NominatedSubContractorInformation');
    }

    public static function getProfitAndAttendancePercentages(ProjectStructure $project)
    {
        $pdo = self::getInstance()->getConnection()->getDbh();

        $stmt = $pdo->prepare("
            SELECT t.sub_project_id, profit_and_attendance_percentage
            FROM " . self::getInstance()->getTableName() . " t
            WHERE t.project_structure_id = {$project->id}
        ");

        $stmt->execute();

        return $stmt->fetchAll(PDO::FETCH_KEY_PAIR);
    }
}