class PostContractRemeasurementClaim extends BasePostContractRemeasurementClaim
{
	public function parentSave(Doctrine_Connection $conn = null)
	{
		parent::save($conn);
	}

	public function save(Doctrine_Connection $conn = null)
	{
		parent::save($conn);

		if ($this->deleted_at == NULL)
		{
			$this->refresh();

			$this->clearBuildUpQuantities();
		}
	}

	public function clearBuildUpQuantities()
	{
		if ( ! $this->has_build_up)
		{
			$type = BillBuildUpQuantityItem::QUANTITY_PER_UNIT_ORIGINAL;

			$query = Doctrine_Query::create()
			->delete('PostContractRemeasurementBuildUpQuantitySummary s')
			->where('s.post_contract_bill_item_rate_id = ?', $this->post_contract_bill_item_rate_id)
			->andWhere('s.bill_column_setting_id = ?', $this->bill_column_setting_id)
			->andWhere('s.type = ?', $type)
			->execute();

			$query = DoctrineQuery::create()
			->delete('PostContractRemeasurementBuildUpQuantityItem i')
			->where('i.post_contract_bill_item_rate_id = ?', $this->post_contract_bill_item_rate_id)
			->andWhere('i.bill_column_setting_id = ?', $this->bill_column_setting_id)
			->andWhere('i.type = ?', $type)
			->execute();
		}
	}
}
