    public function updateUpToDateQty($value, PostContractClaimRevision $revision)
    {
        $typeRef = $this->PostContractStandardClaimTypeReference;

        $typeItem = DoctrineQuery::create()->select('t.qty_per_unit, t.total_per_unit')
            ->from('PostContractBillItemType t')
            ->where('t.post_contract_id = ? AND t.bill_item_id = ? AND t.bill_column_setting_id = ? ',
                array($typeRef->PostContract->id, $this->bill_item_id, $typeRef->bill_column_setting_id))
            ->setHydrationMode(Doctrine_Core::HYDRATE_ARRAY)
            ->limit(1)
            ->fetchOne();

        $previousClaim = $this->getPreviousClaimItemByRevision($revision, Doctrine_Core::HYDRATE_ARRAY);

        if($previousClaim)
        {
            $this->up_to_date_qty = ($value > $typeItem['qty_per_unit']) ? $previousClaim['up_to_date_qty'] : $value;

            $qty = $this->up_to_date_qty - $previousClaim['up_to_date_qty'];
        }
        else
        {
            $qty = $this->up_to_date_qty = ($value > $typeItem['qty_per_unit']) ? $typeItem['qty_per_unit'] : $value;
        }

        $itemRate = DoctrineQuery::create()->select('r.rate')
            ->from('PostContractBillItemRate r')
            ->where('r.post_contract_id = ? AND r.bill_item_id = ? ',
                array($typeRef->PostContract->id, $this->bill_item_id))
            ->setHydrationMode(Doctrine_Core::HYDRATE_ARRAY)
            ->limit(1)
            ->fetchOne();

        $this->updateCurrentAmount(number_format($qty * $itemRate['rate'], 2, '.', ''), $revision, false);
    }

    public function getPreviousClaimItemByRevision(PostContractClaimRevision $revision, $hydrate = null)
    {
        $typeRef = $this->PostContractStandardClaimTypeReference;

        /**Get Previous Rev. */
        $query = DoctrineQuery::create()->select('*')
            ->from('PostContractStandardClaim c')
            ->leftJoin('c.PostContractClaimRevision r ON r.id = c.revision_id')
            ->where('c.claim_type_ref_id = ? AND c.bill_item_id = ? AND r.version = ? AND r.post_contract_id = ?',
                array($typeRef->id, $this->bill_item_id, ($revision->version - 1), $revision->post_contract_id))
            ->limit(1);

        if($hydrate)
        {
            $query->setHydrationMode($hydrate);
        }

        return $query->fetchOne();
    }
