class PreliminariesWorkBasedClaim extends BasePreliminariesWorkBasedClaim
{
    public function save(Doctrine_Connection $conn = NULL)
    {
        // delete available record for PreliminariesTimeBasedClaim
        Doctrine_Query::create()
        ->delete('PreliminariesTimeBasedClaim a')
        ->where('a.post_contract_bill_item_rate_id = ?', $this->post_contract_bill_item_rate_id)
        ->andWhere('a.revision_id = ?', $this->revision_id)
        ->execute();

        parent::save($conn);

        // attach a record to let system know that current edited item has been claim, if total is over than 0
        if ( $this->total > 0 )
        {
            $record = Doctrine_Core::getTable('PreliminariesClaim')->findOneBy('post_contract_bill_item_rate_id', $this->post_contract_bill_item_rate_id);

            if ( ! $record )
            {
                $record                                  = new PreliminariesClaim();
                $record->post_contract_bill_item_rate_id = $this->post_contract_bill_item_rate_id;
                $record->claim_at_revision_id            = $this->revision_id;
                $record->save();
            }
        }
    }
}
