<?php

/**
 * PurchaseOrderTaxTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class PurchaseOrderTaxTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object PurchaseOrderTaxTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('PurchaseOrderTax');
    }

    public static function getAvailableTaxes(PurchaseOrderInformation $purchaseOrderInformation)
    {
        return Doctrine_Query::create()
        ->from('PurchaseOrderTax p')
        ->where('p.purchase_order_information_id = ?', array( $purchaseOrderInformation->id ))
        ->limit(PurchaseOrderTax::ROW_LIMIT)
        ->orderBy('p.id ASC')
        ->execute();
    }

    public static function deleteAllByPurchaseOrder(PurchaseOrderInformation $purchaseOrderInformation)
    {
        $con = self::getInstance()->getConnection();

        $con->fetchAssoc("UPDATE ".self::getInstance()->getTableName()." SET deleted_at = NOW() WHERE
        purchase_order_information_id = ".$purchaseOrderInformation->id." AND deleted_at IS NULL");
    }
}