<?php

/**
 * RFQProjectTable
 *
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class RFQProjectTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object RFQProjectTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('RFQProject');
    }

    public static function getLatestRFQNoCountByProjectId(ProjectStructure $project)
    {
        $pdo = self::getInstance()->getConnection()->getDbh();

        // will select soft delete row as well in order to generate accurate running number for RFQ
        $stmt = $pdo->prepare('SELECT COUNT(s.id) FROM '.self::getInstance()->getTableName().' s
        WHERE s.project_structure_id = '.$project->id);

        $stmt->execute();

        $count = $stmt->fetch(PDO::FETCH_COLUMN, 0);

        return $count + 1;
    }
}