class Regions extends BaseRegions
{
    public function delete(Doctrine_Connection $conn = null)
    {
        if($this->canBeDeleted())
        {
            parent::delete($conn);
        }
        else
        {
            throw new Exception('Region with id: '.$this->id.' is currently in use and cannot be deleted');
        }
    }
    
    public function canBeDeleted()
    {
        return RegionsTable::canBeDeletedById($this->id);
    }

    public function preSave($event)
    {
        parent::preSave($event);

        if(!$this->isNew())
        {
            $modifiedFields = $this->getModified(true);
            if(isset($modifiedFields['currency_code']))
            {
                $currency = Doctrine_Core::getTable('Currency')->findOneByCurrencyCode($modifiedFields['currency_code']);

                if(!$currency)
                {
                    return;
                }
            }
            else
            {
                return;
            }
        }
        else{
            if($currency = Doctrine_Core::getTable('Currency')->findOneByCurrencyCode($this->currency_code))
            {
                return;
            }
            else
            {
                $currency = new Currency();
                $currency->setCurrencyName($this->currency_name);
            }
        }

        $currency->setCurrencyCode($this->currency_code);
        $currency->save();

    }
}
