class ScheduleOfQuantityItemFormulatedColumn extends BaseScheduleOfQuantityItemFormulatedColumn
{
    public function postSave($event)
    {
        if($this->deleted_at == NULL)
        {
            if($this->column_name == ScheduleOfQuantityItem::FORMULATED_COLUMN_QUANTITY)
            {
                $this->clearBuildUpRates();//reset all build up rates
            }
        }

        return parent::postSave($event);
    }

    public function clearBuildUpRates()
    {
        if($this->column_name == ScheduleOfQuantityItem::FORMULATED_COLUMN_QUANTITY and !$this->has_build_up)
        {
            ScheduleOfQuantityItemTable::deleteBuildUpQuantitiesByItemId($this->relation_id);
        }
    }
}
