    public static function calculateTotalCostByScheduleOfRateItemId($scheduleOfRateItemId)
    {
        $query = DoctrineQuery::create()->select('SUM(b.line_total) as total')
            ->from('ScheduleOfRateBuildUpRateItem b')
            ->where('b.schedule_of_rate_item_id = ?', $scheduleOfRateItemId)
            ->limit(1);

        $result = $query->fetchOne();
        $totalCost = $result['total'] ? $result['total'] : 0;

        $updateQuery = DoctrineQuery::create()
            ->update('ScheduleOfRateBuildUpRateSummary')
            ->set('total_cost', '?', $totalCost)
            ->where('schedule_of_rate_item_id = ?', $scheduleOfRateItemId)
            ->andWhere('total_cost <> ?', $totalCost)
            ->execute();

        return $totalCost;
    }
}