class StockInDeliveryOrder extends BaseStockInDeliveryOrder
{

    public function save(Doctrine_Connection $conn = null)
    {
        $isNew = $this->isNew();

        if ( $isNew )
        {
            $userId = sfContext::getInstance()->getUser()->getAttribute('user_id', null, 'sfGuardSecurityUser');

            $this->created_by = $userId;
            $this->updated_by = $userId;
        }

        parent::save($conn);
    }

    public function delete(Doctrine_Connection $conn = null)
    {
        StockInDeliveryOrderItemQuantityTable::deleteRecordsByStockInDeliveryOrder($this);

        return parent::delete($conn);
    }

    public function deleteAttachedFile(Doctrine_Connection $conn = null)
    {
        $this->delivery_order_upload = NULL;

        parent::save($conn);
    }

}