<?php

/**
 * StockOutUsedQuantityTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class StockOutUsedQuantityTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object StockOutUsedQuantityTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('StockOutUsedQuantity');
    }

    public static function generateStockOutNo(ProjectStructure $project)
    {
        $stockOutNo = 0;

        // will get the latest stock out no, then increment it by one
        $stockOut = Doctrine_Query::create()
        ->from('StockOutUsedQuantity i')
        ->where('i.project_structure_id = ?', array($project->id))
        ->orderBy('i.id DESC')
        ->limit(1)
        ->fetchOne();

        // if then assign default one
        if ( $stockOut )
        {
            $stockOutNo = $stockOut->running_number;
        }

        $stockOutNo = $stockOutNo + 1;

        return $stockOutNo;
    }
}