<?php

/**
 * TenderBillElementGrandTotalTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class TenderBillElementGrandTotalTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object TenderBillElementGrandTotalTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('TenderBillElementGrandTotal');
    }

    public static function getByTenderCompanyIdAndBillElementId($tenderCompanyId, $billElementId, $hydrate=null)
    {
        $query = DoctrineQuery::create()->select('*')
            ->from('TenderBillElementGrandTotal e')
            ->where('e.tender_company_id = ?', $tenderCompanyId)
            ->andWhere('e.bill_element_id = ?', $billElementId)
            ->limit(1);

        if(!is_null($hydrate))
        {
            $query->setHydrationMode($hydrate);
        }

        return $query->fetchOne();
    }

    public static function updateGrandTotalByTenderCompanyIdAndElementId($tenderCompanyId, $elementId)
    {
        $pdo = self::getInstance()->getConnection()->getDbh();

        $stmt = $pdo->prepare("UPDATE ".self::getInstance()->getTableName()." SET grand_total = (
            SELECT COALESCE(SUM(r.grand_total), 0)
            FROM ".TenderBillItemRateTable::getInstance()->getTableName()." r
            JOIN ".BillItemTable::getInstance()->getTableName()." i ON r.bill_item_id = i.id
            WHERE r.tender_company_id = ".$tenderCompanyId." AND i.element_id = ".$elementId."
            AND i.project_revision_deleted_at IS NULL AND i.deleted_at IS NULL)
            WHERE tender_company_id = ".$tenderCompanyId." AND bill_element_id = ".$elementId);

        $stmt->execute();
    }
}