class TenderScheduleOfRate extends BaseTenderScheduleOfRate
{
    public function postSave($event)
    {
        DoctrineQuery::create()
            ->update('TenderScheduleOfRate')
            ->set('difference', 'estimation_rate - contractor_rate')
            ->where('id = '.$this->id)
            ->execute();

        parent::postSave($event);

        TenderScheduleOfRateBillItemRateLogTable::insertIntoLog($this);
    }
}
