 */
class TenderScheduleOfRateTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object TenderScheduleOfRateTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('TenderScheduleOfRate');
    }

    public static function getByTenderCompanyIdAndScheduleOfRateBillItemId($tenderCompanyId, $itemId, $hydrate=null)
    {
        $query = DoctrineQuery::create()->select('*')
            ->from('TenderScheduleOfRate r')
            ->where('r.tender_company_id = ?', $tenderCompanyId)
            ->andWhere('r.schedule_of_rate_bill_item_id = ?', $itemId)
            ->limit(1);

        if(!is_null($hydrate))
        {
            $query->setHydrationMode($hydrate);
        }

        return $query->fetchOne();
    }

    public static function flushItemRatesByTenderCompany(TenderCompany $tenderCompany)
    {
        $pdo = self::getInstance()->getConnection()->getDbh();

        $stmt = $pdo->prepare('DELETE FROM ' . self::getInstance()->getTableName() . ' p
        WHERE p.tender_company_id = ' . $tenderCompany->id);

        $stmt->execute();

        return true;
    }
}