class TenderSupplyOfMaterialRate extends BaseTenderSupplyOfMaterialRate
{
    public function postSave($event)
    {
        DoctrineQuery::create()
            ->update('TenderSupplyOfMaterialRate')
            ->set('difference', 'supply_rate - contractor_supply_rate')
            ->set('amount', 'estimated_qty * (supply_rate - contractor_supply_rate)')
            ->where('id = '.$this->id)
            ->execute();

        parent::postSave($event);

        TenderSupplyOfMaterialItemRateLogTable::insertIntoLog($this);
    }
}
