    public static function getByTenderCompanyIdAndSupplyOfMaterialItemId($tenderCompanyId, $itemId, $hydrate=null)
    {
        $query = DoctrineQuery::create()->select('*')
            ->from('TenderSupplyOfMaterialRate r')
            ->where('r.tender_company_id = ?', $tenderCompanyId)
            ->andWhere('r.supply_of_material_item_id = ?', $itemId)
            ->limit(1);

        if(!is_null($hydrate))
        {
            $query->setHydrationMode($hydrate);
        }

        return $query->fetchOne();
    }

    public static function flushItemRatesByTenderCompany(TenderCompany $tenderCompany)
    {
        $pdo = self::getInstance()->getConnection()->getDbh();

        $stmt = $pdo->prepare('DELETE FROM ' . TenderSupplyOfMaterialRateTable::getInstance()->getTableName() . ' p
        WHERE p.tender_company_id = ' . $tenderCompany->id);

        $stmt->execute();

        return true;
    }

}