 */
class VariationOrderClaim extends BaseVariationOrderClaim
{
    const STATUS_PROGRESSING = 1;
    const STATUS_CLOSED = 2;

    public function setStatus($value)
    {
        $status = intval($value);

        $this->_set('status', $status);

        if($status == VariationOrderClaim::STATUS_CLOSED and $this->VariationOrder->ProjectStructure->PostContract->published_type == PostContract::PUBLISHED_TYPE_NEW and
            $openClaimRevision = $this->VariationOrder->ProjectStructure->PostContract->getOpenClaimRevision() and $openClaimRevision->ClaimCertificate->exists())
        {
            $count = Doctrine_Query::create()->select('x.variation_order_claim_id')
                ->from('VariationOrderClaimClaimCertificate x')
                ->andWhere('x.variation_order_claim_id = ?', $this->id)
                ->count();

            if($count==0)
            {
                $xref = new VariationOrderClaimClaimCertificate();
                $xref->variation_order_claim_id = $this->id;
                $xref->claim_certificate_id = $openClaimRevision->ClaimCertificate->id;
                $xref->save();

                if($openClaimRevision->ClaimCertificate->id)
                {
                    $openClaimRevision->ClaimCertificate->save();
                }
            }
        }
    }

    public function save(Doctrine_Connection $conn = null)
    {
        $isNew = $this->isNew();

        parent::save($conn);

        if($this->deleted_at == NULL and $isNew)
        {
            $record = DoctrineQuery::create()->select('c.revision')
                ->from('VariationOrderClaim c')
                ->where('c.variation_order_id = ?', $this->variation_order_id)
                ->andWhere('c.id <> ?', $this->id)
                ->orderBy('c.revision DESC')
                ->limit(1)
                ->setHydrationMode(Doctrine_Core::HYDRATE_ARRAY)
                ->fetchOne();

            if($record)
            {
                $this->revision = $record['revision'] + 1;
                $this->save();

                Doctrine_Query::create()->update('VariationOrderClaim c')
                    ->set('is_viewing', "FALSE")
                    ->where('c.revision < ?', $this->revision)
                    ->andWhere('c.id <> ?', $this->id)
                    ->andWhere('c.variation_order_id = ?', $this->variation_order_id)
                    ->execute();
            }

            if($this->revision > 1)
            {
                $this->clonePreviousClaimItems();
            }
        }
    }

    private function clonePreviousClaimItems()
    {
        $pdo = $this->getTable()->getConnection()->getDbh();

        $previousClaim = $this->getPreviousClaim(Doctrine_Core::HYDRATE_ARRAY);

        if($previousClaim)
        {
            $userId = sfContext::getInstance()->getUser()->getAttribute('user_id', null, 'sfGuardSecurityUser');

            $stmt = $pdo->prepare("INSERT INTO ".VariationOrderClaimItemTable::getInstance()->getTableName()."
            (variation_order_claim_id, variation_order_item_id, up_to_date_quantity, up_to_date_percentage, up_to_date_amount, created_by, updated_by, created_at, updated_at)
            SELECT ".$this->id.", variation_order_item_id, up_to_date_quantity, up_to_date_percentage, up_to_date_amount, ".$userId.", ".$userId.", NOW(), NOW()
            FROM ".VariationOrderClaimItemTable::getInstance()->getTableName()." prev_item
            WHERE  prev_item.variation_order_claim_id = ".$previousClaim['id']." AND prev_item.deleted_at IS NULL");

            $stmt->execute(array());
        }
    }

    public function canBeDeleted()
    {
        $record = DoctrineQuery::create()->select('c.id')
            ->from('VariationOrderClaim c')
            ->where('c.variation_order_id = ?', $this->variation_order_id)
            ->andWhere('c.revision > ?', $this->revision)
            ->limit(1)
            ->setHydrationMode(Doctrine_Core::HYDRATE_ARRAY)
            ->fetchOne();

        return $record ? false : true;
    }

    public function delete(Doctrine_Connection $conn = null)
    {
        if($this->canBeDeleted())
        {
            $previousClaim = $this->getPreviousClaim();

            if($previousClaim)
            {
                $previousClaim->is_viewing = true;
                $previousClaim->save();
            }

            Doctrine_Query::create()->delete('VariationOrderClaimItem i')
                ->where('i.variation_order_claim_id = ?', $this->id)
                ->execute();

            parent::delete($conn);
        }
        else
        {
            throw new Exception("variation order claim with id: ".$this->id." cannot be deleted.");
        }
    }

    public function getPreviousClaim($hydrate = null)
    {
        $query = DoctrineQuery::create()->select('c.*')
            ->from('VariationOrderClaim c')
            ->where('c.variation_order_id = ?', $this->variation_order_id)
            ->andWhere('c.revision = ?', $this->revision - 1)
            ->limit(1);

        if($hydrate)
        {
            $query->setHydrationMode($hydrate);

        }

        return $query->fetchOne();
    }

    public function setAsViewingClaim()
    {
        $variationOrder = $this->VariationOrder;

        $viewingClaim = $variationOrder->getViewingClaim();

        $this->is_viewing = true;
        $this->save();

        if ( $viewingClaim && $viewingClaim->id != $this->id )
        {
            $viewingClaim->is_viewing = false;
            $viewingClaim->save();
        }
    }
}
