<?php

/**
 * WorkCategoryTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class WorkCategoryTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object WorkCategoryTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('WorkCategory');
    }

    public static function canBeDeletedById($id)
    {
        $pdo = self::getInstance()->getConnection()->getDbh();

        $stmt = $pdo->prepare("SELECT COUNT(id) FROM ".ProjectMainInformationTable::getInstance()->getTableName()."
        WHERE work_category_id = ".$id);

        $stmt->execute();

        $count = $stmt->fetch(PDO::FETCH_COLUMN, 0);

        return $count > 0 ? false : true;
    }
}